  { text: "Qual a conduta correta ao visualizar faixa de pedestres sem semáforo?", options: ["Buzinar para que o pedestre acelere", "Ultrapassar pela direita", "Acelerar para não parar", "Parar antes da faixa e dar preferência ao pedestre"], correctOption: 3, difficultyLevel: 3 },
  { text: "Em rodovia de pista simples, quando há faixa adicional à direita, ela é destinada a:", options: ["Estacionamento", "Parada de emergência", "Faixa de pedestres", "Ultrapassagens"], correctOption: 0, difficultyLevel: 3 },
  { text: "Como proceder em caso de aquaplanagem em alta velocidade?", options: ["Virar o volante rapidamente", "Acelerar para sair da poça", "Frear forte para ganhar aderência", "Soltar acelerador, não frear bruscamente e manter volante firme"], correctOption: 1, difficultyLevel: 3 },
  { text: "Ao ultrapassar veículo lento, qual distância lateral mínima deve ser mantida?", options: ["0,5 metro", "1 metro", "2 metros", "1,5 metros"], correctOption: 2, difficultyLevel: 3 },
  { text: "Em trajeto com neblina densa, deve‑se usar:", options: ["Farol alto + luz de neblina traseiro", "Somente luz de ré", "Pisca‑alerta", "Farol baixo + luz de neblina dianteiro"], correctOption: 3, difficultyLevel: 3 },
  { text: "Qual a sequência correta de prioridades em um trevo em cruz de nível?", options: ["Quem chega primeiro → sempre pedestre → sempre ciclista", "Acelerar antes de entrar → reduzir depois", "Ultrapassar veículos que param", "Via preferencial → circulando pela rotatória → ingressando"], correctOption: 0, difficultyLevel: 3 },
  { text: "Ao perceber defeito no sistema de freios, o condutor deve:", options: ["Continuar viagem lentamente", "Desligar o motor na via", "Ultrapassar para encontrar oficina", "Parar em local seguro, sinalizar e acionar socorro"], correctOption: 1, difficultyLevel: 3 },
  { text: "Em estrada sinuosa, para evitar tombamento de veículo alto, deve‑se:", options: ["Acelerar na entrada da curva", "Frear forte durante a curva", "Ultrapassar outros veículos", "Reduzir velocidade antes da curva e não frear no meio"], correctOption: 2, difficultyLevel: 3 },
  { text: "Como proceder se avistar um animal solto na pista?", options: ["Buzinar para espantar o animal", "Fazer retorno imediato", "Acelerar para passar rápido", "Reduzir gradativamente e sinalizar atrás"], correctOption: 3, difficultyLevel: 3 },
  { text: "Em caso de colisão sem vítimas, o condutor deve:", options: ["Fugir do local", "Mover veículos imediatamente sem sinalizar", "Buzinar continuamente", "Sinalizar o local e aguardar autoridade competente"], correctOption: 0, difficultyLevel: 3 },
  { text: "Como calcular tempo de reação e frenagem a 80 km/h (reação 1s)?", options: ["Distância = 80 m + frenagem", "Distância = 5 m fixa", "Distância = 10% da velocidade", "Distância = 22,2 m (reação) + frenagem"], correctOption: 1, difficultyLevel: 3 },
  { text: "Ao conduzir em via de mão única, como sinalizar retorno permitido?", options: ["Buzinar antes de retornar", "Desligar luz indicadora", "Fazer retorno em qualquer local", "Usar seta esquerda e aguardar brecha no tráfego"], correctOption: 2, difficultyLevel: 3 },
  { text: "Para estacionar em subida com guia elevada, qual posição das rodas?", options: ["Viradas para rua", "Paralelas à guia", "Não importa", "Viradas para calçada para conter rolamento"], correctOption: 3, difficultyLevel: 3 },
  { text: "Ao avistar barreira policial, o condutor deve:", options: ["Acelerar para evitar fiscalização", "Ultrapassar pela direita", "Buzinar até parar", "Reduzir velocidade, parar no local indicado e apresentar documentos"], correctOption: 0, difficultyLevel: 3 },
  { text: "Em congestionamento intenso, para liberar cruzamentos, deve‑se:", options: ["Avançar para garantir prioridade", "Ultrapassar pela calçada", "Estacionar no cruzamento", "Não avançar se não houver espaço livre"], correctOption: 1, difficultyLevel: 3 },
  { text: "Qual a conduta ao ser atingido por projétil de outro veículo?", options: ["Reagir perseguindo o outro veículo", "Ignorar e continuar", "Ultrapassar para buscar testemunha", "Encostar em local seguro, sinalizar e chamar a polícia"], correctOption: 2, difficultyLevel: 3 },
  { text: "Em vias com faixa exclusiva de ônibus, ciclistas podem utilizar essa faixa?", options: ["Sim, sempre", "Somente em velocidade reduzida", "Quando não houver ônibus", "Não, exceto se sinalizado regulamentarmente"], correctOption: 3, difficultyLevel: 3 },
  { text: "Como proceder ao avistar pista parcialmente alagada?", options: ["Avançar em segunda marcha", "Frear forte antes de entrar na água", "Acelerar para atravessar rápido", "Reduzir a velocidade antes e durante a travessia"], correctOption: 0, difficultyLevel: 3 },
  { text: "Em rota de emergência (resgate), veículos devem:", options: ["Buzinar para se escorar atrás", "Ultrapassar emergenciais", "Ignorar sirene", "Dar passagem imediata e desobstruir pista"], correctOption: 1, difficultyLevel: 3 },
  { text: "No retorno em rótula (rotatória simples), quem tem preferência?", options: ["Quem chega primeiro", "Quem estiver na direita", "Pedestres", "Quem já estiver circulando"], correctOption: 2, difficultyLevel: 3 },
  { text: "Em rodovia com acostamento estreito, o ciclista deve:", options: ["Circular no acostamento mesmo estreito", "Andar na contramão", "Ultrapassar veículos", "Usar a faixa de rolamento mantendo-se à direita"], correctOption: 3, difficultyLevel: 3 },
  { text: "Como sinalizar emergência mecânica em via urbana?", options: ["Buzinar continuamente", "Ligar farol alto", "Ignorar sinalização", "Acionar pisca-alerta e posicionar triângulo a 30m"], correctOption: 0, difficultyLevel: 3 },
  { text: "Ao transitar próximo a curvas fechadas à montante, o ciclista deve:", options: ["Atravessar curva rapidamente", "Ultrapassar veículos por dentro", "Andar pela calçada", "Manter distância do veículo à frente e usar sinalização manual"], correctOption: 1, difficultyLevel: 3 },
  { text: "Qual a conduta em caso de falha dos faróis durante a noite?", options: ["Seguir devagar sem luz", "Usar lanterna de celular", "Buzinar para alertar pedestres", "Parar em local iluminado e acionar socorro"], correctOption: 2, difficultyLevel: 3 },
  { text: "No cruzamento em ‘T’, sem semáforo ou placa, quem tem preferência?", options: ["Veículo que estiver à esquerda", "Quem chegar primeiro", "Veículo que estiver à direita", "Veículo que trafega pela via principal"], correctOption: 3, difficultyLevel: 3 },
  { text: "Ao realizar balão (retorno em via), o condutor deve:", options: ["Ultrapassar rapidamente", "Buzinar antes de virar", "Ignorar pedestres", "Verificar se não há proibição e sinalizar a manobra"], correctOption: 0, difficultyLevel: 3 },
  { text: "Qual a distância recomendada para ultrapassar ambulância com sinais sonoros?", options: ["50 cm", "1 metro", "5 metros", "2 metros"], correctOption: 1, difficultyLevel: 3 },
  { text: "Como proceder ao avistar poste caído sobre a via?", options: ["Ultrapassar por baixo", "Acelerar para passar rápido", "Buzinar para alertar outros", "Parar antes, sinalizar e acionar emergência local"], correctOption: 2, difficultyLevel: 3 },
  { text: "Em trechos estreitos de mão dupla sem acostamento, qual a regra de ultrapassagem?", options: ["Ultrapassar sempre pela direita", "Buzinar para liberar espaço", "Acelerar para finalizar rápido", "Não ultrapassar se não houver segurança"], correctOption: 3, difficultyLevel: 3 },
  { text: "Ao reduzir marcha em descida íngreme, o condutor deve:", options: ["Frear forte apenas no início", "Manter neutro e frear", "Desligar motor", "Frear com motor e usar freio pouco a pouco"], correctOption: 0, difficultyLevel: 3 },
  { text: "Qual a conduta ao presenciar violência no trânsito entre terceiros?", options: ["Intervir fisicamente", "Ultrapassar os envolvidos", "Buzinar até separar", "Parar em local seguro e acionar a polícia sem se envolver"], correctOption: 1, difficultyLevel: 3 },
  { text: "Em estrada de pista dupla, se houver chuva forte, deve‑se:", options: ["Acelerar para secar pneus", "Manter velocidade de cruzeiro", "Ultrapassar veículos lentos", "Reduzir velocidade e aumentar distância de seguimento"], correctOption: 2, difficultyLevel: 3 },
  { text: "Em rotatória sinalizada com faixa de pedestre, quem tem preferência?", options: ["Veículo que entra", "Veículo que circula", "Quem buzinar", "Pedestre na faixa"], correctOption: 3, difficultyLevel: 3 },
  { text: "Em local com faixa de pedestre e semáforo apagado, o que fazer?", options: ["Passar devagar se não houver veículos", "Buzinar para o pedestre sair da faixa", "Ultrapassar pela direita", "Parar e aguardar o pedestre atravessar"], correctOption: 0, difficultyLevel: 3 },
  { text: "Ao aproximar-se de interseção em cruz sem placa, quem tem preferência?", options: ["Quem estiver na via principal", "Quem vier pela esquerda", "Quem buzinar primeiro", "Quem vier pela direita"], correctOption: 1, difficultyLevel: 3 },
  { text: "Qual a distância mínima para sinalizar uma mudança de faixa?", options: ["1 metro antes", "No momento da manobra", "10 metros depois", "5 metros antes"], correctOption: 2, difficultyLevel: 3 },
  { text: "Em descida íngreme com freio fraco, a melhor prática é:", options: ["Desligar o motor e usar freio de mão", "Frear forte apenas no início", "Neutralizar o câmbio", "Engatar marcha reduzida e usar freio motor"], correctOption: 3, difficultyLevel: 3 },
  { text: "Como proceder ao avistar faixa de pedestres elevada (lombofaixa)?", options: ["Ultrapassar rapidamente", "Ignorar se não houver pedestre", "Buzinar para alertar", "Reduzir a velocidade antes e durante a travessia"], correctOption: 0, difficultyLevel: 3 },
  { text: "Qual conduta em obra na via sem sinalização avançada?", options: ["Manter velocidade e buzinar", "Ultrapassar pela contramão", "Ignorar barreiras", "Reduzir a velocidade ao avistar, usar pisca-alerta"], correctOption: 1, difficultyLevel: 3 },
  { text: "Se o veículo patinar em neve ou gelo, deve‑se:", options: ["Frear com força máxima", "Virar volante rapidamente", "Desligar o controle de tração", "Manter volante firme e acelerar suavemente"], correctOption: 2, difficultyLevel: 3 },
  { text: "Em que situação é permitido transitar com farol de neblina traseiro ligado?", options: ["Chuva leve", "Noite escura sem neblina", "Sempre que quiser", "Neblina densa"], correctOption: 3, difficultyLevel: 3 },
  { text: "Ao avistar uma viatura de emergência com sirene ligada atrás, o condutor deve:", options: ["Acelerar para não atrapalhar", "Buzinar para sair da frente", "Ignorar sinais sonoros", "Encostar o veículo e parar"], correctOption: 0, difficultyLevel: 3 },
  { text: "Como sinalizar erro de rota com restrição de tráfego?", options: ["Parar no meio da via", "Recuar até o cruzamento anterior", "Ultrapassar veículos lentos", "Fazer retorno no primeiro local permitido e usar seta"], correctOption: 1, difficultyLevel: 3 },
  { text: "Em pista dupla, se carro da esquerda frear bruscamente, o condutor atrás deve:", options: ["Ultrapassar pela direita", "Buzinar até sair da frente", "Desligar o motor", "Reduzir e manter distância segura"], correctOption: 2, difficultyLevel: 3 },
  { text: "Qual é a conduta ao passar por ponte estreita de mão dupla?", options: ["Acelerar para atravessar rápido", "Ultrapassar pela calçada", "Ignorar o outro veículo", "Ceder passagem ao veículo que vem na direção oposta"], correctOption: 3, difficultyLevel: 3 },
  { text: "Em via alagada, o condutor deve:", options: ["Acelerar para não parar na água", "Ultrapassar outros veículos", "Manter marcha neutra", "Não atravessar se a água atingir a altura do eixo"], correctOption: 0, difficultyLevel: 3 },
  { text: "Qual a sequência correta para partida em rampa com carros atrás?", options: ["Soltar embreagem e freio juntos", "Acelerar sem embreagem", "Desligar freio de mão e acelerar", "Freio de mão + embreagem no ponto de contato + liberar freio"], correctOption: 1, difficultyLevel: 3 },
  { text: "Em acidente sem vítima bloqueando a via, o condutor deve:", options: ["Fugir para evitar multa", "Parar sem sinalizar", "Mover os veículos sem sinalização", "Sinalizar o local e liberar o trânsito"], correctOption: 2, difficultyLevel: 3 },
  { text: "Ao visualizar placa de cruzamento de linhas férreas sem cancela, deve‑se:", options: ["Acelerar para atravessar", "Ignorar a placa", "Buzinar continuamente", "Reduzir, olhar ambos os lados e cruzar com cautela"], correctOption: 3, difficultyLevel: 3 },
  { text: "Em via com ciclovia e sem faixa de pedestres, o pedestre deve:", options: ["Cortar a via no ponto mais próximo", "Andar na ciclovia", "Ultrapassar ciclistas", "Não cruzar fora de faixa e usar trajetos adequados"], correctOption: 0, difficultyLevel: 3 },
  { text: "Quando há lombada e quebra‑molas consecutivos, o que é recomendado?", options: ["Acelerar ao passar", "Manter cruise control", "Ignorar sinalizações", "Reduzir velocidade antes de cada lombada"], correctOption: 1, difficultyLevel: 3 },
  { text: "Em situação de neblina e sem neblina traseira, o condutor deve:", options: ["Manter apenas o farol alto", "Farol de milha", "Somente painel aceso", "Ligar a luz de neblina traseira apenas se a visibilidade <50 m"], correctOption: 2, difficultyLevel: 3 },
  { text: "Ao conduzir em via dentro de túnel sem iluminação, deve‑se:", options: ["Usar farol alto", "Desligar faróis para economizar energia", "Ultrapassar veículos lentos", "Manter farol baixo ligado e reduzir velocidade"], correctOption: 3, difficultyLevel: 3 },
  { text: "Em local com estreitamento de pista sinalizado, o veículo que chega primeiro deve:", options: ["Parar antes do sinal e aguardar", "Ultrapassar pela outra faixa", "Buzinar para entrar", "Avançar e ocupar o estreitamento"], correctOption: 0, difficultyLevel: 3 },
  //Questões para ciclistas
  { text: "Bicicletas elétricas com potência nominal máxima de até 350 Watts e velocidade máxima de 25 km/h são equiparadas a:", options: ["Motocicletas.", "Bicicletas convencionais.", "Ciclomotores.", "Veículos automotores."], correctOption: 1, difficultyLevel: 3 },
  { text: "Condutores de bicicletas elétricas equiparadas a bicicletas convencionais precisam de CNH para circular?", options: ["Sim, em qualquer via.", "Sim, apenas em rodovias.", "Não.", "Depende da potência da bicicleta elétrica."], correctOption: 2, difficultyLevel: 3 },
  { text: "Ciclomotores (duas ou três rodas, motor de até 50 cm³ e velocidade máxima de 50 km/h) precisam de emplacamento e licenciamento para circular?", options: ["Sim.", "Não.", "Apenas em rodovias.", "Apenas se o condutor for menor de idade."], correctOption: 0, difficultyLevel: 3 },
  { text: "Qual a idade mínima para conduzir um ciclomotor?", options: ["16 anos.", "18 anos.", "21 anos.", "Não há idade mínima especificada."], correctOption: 1, difficultyLevel: 3 },
  { text: "O uso de capacete é obrigatório para condutores e passageiros de:", options: ["Bicicletas convencionais.", "Ciclomotores.", "Bicicletas elétricas equiparadas a bicicletas.", "Patinetes elétricos."], correctOption: 1, difficultyLevel: 3 },
  { text: "Ciclomotores podem circular em ciclovias e ciclo faixas?", options: ["Sim, sempre.", "Não, sua circulação é restrita às vias urbanas.", "Apenas em ciclovias afastadas do tráfego de veículos.", "Depende da sinalização local."], correctOption: 1, difficultyLevel: 3 },
  { text: "Bicicletas elétricas equiparadas a bicicletas convencionais podem circular em calçadas?", options: ["Sim, sempre.", "Excepcionalmente, quando não houver ciclovia, ciclo faixa ou acostamento, ou quando a segurança não permitir a circulação na via, dando sempre preferência aos pedestres.", "Sim, desde que em baixa velocidade.", "Não, é sempre proibido."], correctOption: 1, difficultyLevel: 3 },
  { text: "Qual a sinalização noturna obrigatória para ciclomotores?", options: ["Farol dianteiro, lanterna traseira de luz vermelha e sinalização de freios.", "Apenas farol dianteiro e lanterna traseira.", "A mesma sinalização de bicicletas convencionais.", "Não há sinalização noturna obrigatória."], correctOption: 0, difficultyLevel: 3 },
  { text: "Qual a velocidade máxima permitida para ciclomotores em vias urbanas, conforme a legislação geral?", options: ["30 km/h.", "50 km/h.", "60 km/h.", "70 km/h."], correctOption: 1, difficultyLevel: 3 },
  { text: "Ao atravessar uma faixa de pedestre com uma bicicleta elétrica equiparada a bicicleta, o condutor deve:", options: ["Atravessar pedalando rapidamente.", "Sinalizar com a mão e atravessar pedalando com cuidado.", "Descer da bicicleta e empurrá-la ao atravessar a faixa de pedestre.", "Atravessar pedalando apenas se não houver pedestres por perto."], correctOption: 2, difficultyLevel: 3 },
  //Conscientização e Atualização para Motoristas
  { text: "Qual a conduta mais segura ao se aproximar de um cruzamento com sinal verde, mas com trânsito congestionado à frente?", options: ["Acelerar para aproveitar o sinal", "Buzinar para alertar os outros condutores", "Avaliar a possibilidade de transpor o cruzamento sem interromper o fluxo da via transversal, mesmo que o sinal fique amarelo ou vermelho", "Parar imediatamente, mesmo que no meio do cruzamento, se o sinal ficar amarelo"], correctOption: 2, difficultyLevel: 3 },
  { text: "Segundo as recentes alterações no CTB, qual a validade da CNH para condutores com idade igual ou superior a 50 anos e inferior a 70 anos?", options: ["3 anos", "5 anos", "7 anos", "10 anos"], correctOption: 1, difficultyLevel: 3 },
  { text: "De acordo com o CTB, o que é considerado um 'veículo automotor'?", options: ["Todo veículo a motor de propulsão que circule por seus próprios meios, exceto os movidos a propulsão humana e os de tração animal", "Qualquer veículo que se mova", "Apenas carros e motocicletas", "Veículos com mais de quatro rodas"], correctOption: 0, difficultyLevel: 3 },
  { text: "Segundo as novas regras do CTB, o que ocorreu com a pontuação da CNH para suspensão do direito de dirigir?", options: ["O limite de pontos foi unificado para todos os condutores", "Foram criados limites de pontuação variáveis (20, 30 ou 40 pontos) dependendo da quantidade de infrações gravíssimas cometidas nos últimos 12 meses", "O sistema de pontuação foi abolido", "O limite de pontos para suspensão aumentou para todos os condutores"], correctOption: 1, difficultyLevel: 3 },
  { text: "Qual a responsabilidade do motorista em relação à segurança dos passageiros, especialmente crianças?", options: ["Garantir que todos os passageiros utilizem o cinto de segurança ou o dispositivo de retenção adequado para a idade e peso, além de adotar uma condução segura e responsável", "Apenas alertar os passageiros sobre a importância do cinto de segurança", "Apenas se preocupar com a segurança das crianças menores de 10 anos", "Considerar que a segurança é responsabilidade exclusiva dos passageiros"], correctOption: 0, difficultyLevel: 3 },
  { text: "Dirigir utilizando telefone celular é considerado:", options: ["Uma distração tolerável em semáforos", "Permitido com fone de ouvido", "Infração gravíssima", "Infração leve"], correctOption: 2, difficultyLevel: 3 },
  { text: "O uso de fones de ouvido enquanto dirige:", options: ["Melhora a concentração em ambientes ruidosos", "É permitido para atender chamadas rapidamente", "É considerado infração média", "Não interfere na atenção do condutor"], correctOption: 2, difficultyLevel: 3 },
  { text: "Não sinalizar com antecedência a mudança de direção (dar a seta) é:", options: ["Uma prática comum em trânsito congestionado", "Desnecessário se não houver outros veículos próximos", "Infração grave", "Apenas uma falta de cortesia"], correctOption: 2, difficultyLevel: 3 },
  { text: "Estacionar o veículo em fila dupla é:", options: ["Permitido por tempo breve para desembarque rápido", "Infração grave", "Aceitável em ruas largas", "Uma forma de otimizar o espaço em locais cheios"], correctOption: 1, difficultyLevel: 3 },
  { text: "As vagas de estacionamento sinalizadas com o Símbolo Internacional de Acesso (SIA), indicativas de PCD, podem ser utilizadas por:", options: ["Qualquer pessoa com dificuldade de locomoção, mesmo sem credencial", "Veículos que transportem pessoas com deficiência com mobilidade reduzida, devidamente identificados com a credencial específica", "Familiares de pessoas com deficiência, mesmo que a pessoa não esteja no veículo", "Qualquer motorista que encontre a vaga livre"], correctOption: 1, difficultyLevel: 3 },
  //Conscientização e Segurança no Trânsito para Ensino Médio
  { text: "Quais as consequências legais para um adolescente que é flagrado dirigindo um veículo automotor sem possuir a CNH?", options: ["Medidas socioeducativas previstas no ECA e responsabilização dos pais ou responsáveis", "Apenas uma advertência verbal por ser menor de idade", "A mesma multa aplicada a um adulto com CNH vencida", "Nenhuma consequência imediata, pois a responsabilidade é dos pais"], correctOption: 0, difficultyLevel: 3 },
  { text: "Quais as possíveis consequências para os pais ou responsáveis que permitem ou negligenciam que um adolescente sem CNH dirija um veículo?", options: ["Responsabilização legal por omissão, podendo responder por crimes de trânsito em caso de acidente causado pelo menor", "Apenas uma advertência por escrito dos órgãos de trânsito", "Nenhuma consequência se o adolescente dirigir com cuidado", "Apenas a obrigação de matricular o adolescente em um curso de direção"], correctOption: 0, difficultyLevel: 3 },
  { text: "Quais os riscos de um adolescente utilizar o carro dos pais sem CNH, mesmo que seja apenas para dar uma 'volta rápida' no quarteirão?", options: ["Perda do controle do veículo, envolvimento em acidentes, responsabilidade civil e criminal em caso de danos ou ferimentos a terceiros, além das consequências legais", "Apenas o risco de ser repreendido pelos pais", "Nenhum risco significativo se o adolescente dirigir com cuidado e em baixa velocidade", "Apenas o risco de danificar o veículo"], correctOption: 0, difficultyLevel: 3 },
  { text: "Quais os principais riscos de um adolescente pegar o carro dos pais sem ter a CNH e sem o conhecimento adequado das leis de trânsito?", options: ["Alto risco de causar acidentes por imperícia, desconhecimento das normas, além das consequências legais para si e para os pais", "Apenas o risco de danificar o veículo dos pais", "Nenhum risco significativo se o adolescente for um 'bom motorista'", "Apenas o risco de ser parado pela polícia e ter o carro apreendido"], correctOption: 0, difficultyLevel: 3 },
  { text: "Quais as implicações de um adolescente ser pego dirigindo sem CNH em relação ao futuro processo para obter a habilitação?", options: ["Pode haver impedimentos ou exigências adicionais no processo de obtenção da CNH, além das consequências legais imediatas", "Não há nenhuma implicação futura para a obtenção da CNH", "O adolescente terá prioridade ao se matricular em um curso de direção", "O tempo em que dirigiu ilegalmente será considerado como experiência prática"], correctOption: 0, difficultyLevel: 3 },
  //Pedestre
  { text: "Qual a importância de respeitar o ciclista?", options: ["Compartilhar o espaço público de forma segura e cortês, evitando colisões.", "Considerar que a prioridade é sempre dos pedestres.", "Não é tão importante quanto respeitar os motoristas.", "Apenas desviar quando eles se aproximarem."], correctOption: 0, difficultyLevel: 3 },
  { text: "Qual a importância de utilizar roupas claras ou com elementos refletivos ao caminhar à noite?", options: ["Aumentar a visibilidade para os motoristas.", "Apenas para ter um visual mais elegante.", "Para se proteger do frio.", "Não faz diferença na segurança."], correctOption: 0, difficultyLevel: 3 },
  { text: "Qual a importância de conhecer os sinais sonoros dos veículos de emergência?", options: ["Para imitar o som com os amigos.", "Apenas para saber que há uma emergência acontecendo.", "Identificar sua aproximação e liberar a passagem com segurança.", "Não são tão importantes para pedestres."], correctOption: 2, difficultyLevel: 3 },
  { text: "Qual a importância de evitar atravessar a rua atrás de ônibus ou caminhões parados?", options: ["A visibilidade do pedestre e dos outros motoristas pode estar comprometida, aumentando o risco de atropelamento.", "Apenas para não se sujar com a fumaça.", "Porque esses veículos podem arrancar repentinamente.", "Não há problema em atravessar atrás de veículos grandes parados."], correctOption: 0, difficultyLevel: 3 },
  { text: "Qual a conduta segura para o pedestre ao se aproximar de um cruzamento com grande circulação de bicicletas?", options: ["Redobrar a atenção, observar os ciclistas e atravessar somente quando seguro.", "Tentar atravessar antes das bicicletas.", "Correr para não ser atingido pelas bicicletas.", "Considerar que a prioridade é sempre dos pedestres."], correctOption: 0, difficultyLevel: 3 },
  { text: "Qual a importância de utilizar a faixa elevada para pedestre?", options: ["Apenas para evitar subir e descer da calçada.", "Oferecer uma travessia mais segura, pois os veículos são obrigados a reduzir a velocidade.", "Para praticar atividade física.", "Não são tão comuns na cidade."], correctOption: 1, difficultyLevel: 3 },
  { text: "Qual a importância de ensinar as crianças sobre segurança no trânsito?", options: ["Formar pedestres e futuros condutores conscientes e seguros.", "Apenas para que não incomodem os pais na rua.", "Para que aprendam a identificar os veículos.", "Não é uma preocupação importante na infância."], correctOption: 0, difficultyLevel: 3 },
  { text: "Qual a principal mensagem da segurança para pedestre?", options: ["A culpa dos atropelamentos é sempre dos motoristas.", "Os atropelamentos são inevitáveis.", "A segurança depende da atenção constante, do respeito às leis e da adoção de comportamentos preventivos.", "A pressa justifica atravessar a rua de qualquer jeito."], correctOption: 2, difficultyLevel: 3 },
  { text: "Qual a importância de prestar atenção ao redor ao caminhar na rua?", options: ["Não é tão importante quanto olhar para o celular.", "Apenas para não se perder.", "Para observar as vitrines das lojas.", "Identificar possíveis perigos, como veículos em movimento, buracos e obstáculos."], correctOption: 3, difficultyLevel: 3 },
  { text: "Qual a atitude correta ao presenciar um acidente envolvendo pedestre?", options: ["Sinalizar o local, acionar os serviços de emergência (190 ou 193) e prestar socorro à vítima, se souber como fazê-lo.", "Apenas observar o que aconteceu.", "Tentar remover a vítima do local.", "Culpar alguém pelo acidente."], correctOption: 0, difficultyLevel: 3 },
  { text: "Qual a prioridade em um cruzamento não sinalizado?", options: ["O pedestre que chegar primeiro.", "O veículo que estiver em maior velocidade.", "O veículo que vier pela direita do pedestre (em relação ao fluxo dos veículos).", "Todos devem parar e negociar a passagem."], correctOption: 2, difficultyLevel: 3 },
  { text: "Qual a conduta segura para o pedestre ao atravessar uma rua onde há obras?", options: ["Observar atentamente a sinalização, utilizar as passagens seguras indicadas e seguir as orientações dos trabalhadores.", "Tentar atravessar pelo caminho mais curto, ignorando a sinalização.", "Correr para passar pela área de obras rapidamente.", "Apenas atravessar se não houver máquinas em movimento."], correctOption: 0, difficultyLevel: 3 },
  { text: "Qual a importância de utilizar as faixas elevadas para pedestres?", options: ["Apenas para evitar subir e descer da calçada.", "Oferecer uma travessia mais segura, pois os veículos são obrigados a reduzir a velocidade.", "Para praticar atividade física.", "Não são tão comuns na cidade."], correctOption: 1, difficultyLevel: 3 },
  { text: "Qual a conduta segura para o pedestre ao caminhar próximo a ciclovias ou ciclofaixas?", options: ["Manter-se na área destinada aos pedestres e atravessar a ciclovia/ciclofaixa com atenção, certificando-se de que não há ciclistas se aproximando.", "Caminhar na ciclovia/ciclofaixa para ter mais espaço.", "Atravessar a ciclovia/ciclofaixa correndo.", "Considerar que a prioridade é sempre dos pedestres."], correctOption: 0, difficultyLevel: 3 },
  { text: "Qual a principal mensagem da segurança para pedestres?", options: ["A culpa dos atropelamentos é sempre dos motoristas.", "Os atropelamentos são inevitáveis.", "A segurança depende da atenção constante, do respeito às leis e da adoção de comportamentos preventivos.", "A pressa justifica atravessar a rua de qualquer jeito."], correctOption: 2, difficultyLevel: 3 },
  { text: "Qual a atitude correta ao presenciar um acidente envolvendo pedestres?", options: ["Sinalizar o local, acionar os serviços de emergência (190 ou 193) e prestar socorro à vítima, se souber como fazê-lo.", "Apenas observar o que aconteceu.", "Tentar remover a vítima do local.", "Culpar alguém pelo acidente."], correctOption: 0, difficultyLevel: 3 },
  //Ensino Fundamental
  { text: "Onde os pedestre devem andar?", options: ["No meio da rua, correndo.", "Na calçada, com cuidado.", "No acostamento, se não tiver calçada.", "Em qualquer lugar, sem prestar atenção."], correctOption: 1, difficultyLevel: 1 },
  { text: "O que um ciclista NÃO deve fazer?", options: ["Usar capacete para se proteger.", "Andar de bicicleta na calçada.", "Sinalizar com as mãos antes de virar.", "Respeitar os semáforos."], correctOption: 1, difficultyLevel: 1 },
  { text: "Quando a luz do semáforo para os carros está VERMELHA, o que o pedestre pode fazer?", options: ["Atravessar correndo sem olhar.", "Esperar no cantinho da calçada.", "Atravessar a faixa de pedestre com cuidado, olhando para os dois lados.", "Continuar esperando, mesmo que não venham carros."], correctOption: 2, difficultyLevel: 1 },
  { text: "Qual destes equipamentos é OBRIGATÓRIO para andar de bicicleta com segurança?", options: ["Campainha.", "Cesta de compras na frente.", "Capacete na cabeça.", "Luvas."], correctOption: 2, difficultyLevel: 1 },
  { text: "Para que serve a faixa de pedestre?", options: ["Para os carros estacionarem.", "Para as pessoas atravessarem a rua com mais segurança.", "Para as crianças brincarem de correr.", "Para os ciclistas descansarem."], correctOption: 1, difficultyLevel: 1 },
  { text: "O que a luz VERDE do semáforo indica para os carros?", options: ["Que os carros podem seguir em frente.", "Que os carros precisam parar.", "Que os pedestres podem atravessar.", "Que os ciclistas devem ter cuidado."], correctOption: 0, difficultyLevel: 1 },
  { text: "Se você estiver andando de bicicleta e precisar virar à direita, o que você deve fazer com o braço?", options: ["Apontar para cima.", "Colocar a mão na cabeça.", "Estender o braço direito para o lado.", "Abaixar o braço esquerdo."], correctOption: 2, difficultyLevel: 1 },
  { text: "É seguro andar de bicicleta entre os veículos que estão circulando?", options: ["Sim, se a bicicleta for rápida.", "Sim, se o carro estiver devagar.", "Sim, se você estiver usando capacete.", "Não, é perigoso e pode causar sinistro."], correctOption: 3, difficultyLevel: 1 },
  { text: "O que você deve fazer antes de atravessar uma rua?", options: ["Fechar os olhos e correr.", "Atravessar olhando para o celular.", "Olhar para os dois lados para ver se vem veículos na sua direção.", "Apenas seguir em frente sem prestar atenção."], correctOption: 2, difficultyLevel: 1 },
  { text: "Qual é o lugar seguro para as crianças serem transportadas no carro?", options: ["No banco da frente, no colo de um adulto.", "No banco de trás, sem cinto de segurança.", "No banco de trás, com dispositivo de segurança adequada para a idade.", "Em pé no banco de trás."], correctOption: 2, difficultyLevel: 1 },
  { text: "O que indica que há uma faixa de pedestre?", options: ["Um círculo vermelho com um risco no meio.", "Linhas brancas horizontais pintadas no asfalto.", "Um triângulo amarelo com um ponto de exclamação.", "Um quadrado azul com uma seta."], correctOption: 1, difficultyLevel: 1 },
  { text: "O que significa a luz AMARELA do semáforo para os veículos?", options: ["Que podem acelerar para passar logo.", "Que precisam diminuir a velocidade e preparar para parar.", "Que já podem atravessar o cruzamento.", "Que os pedestres devem começar a atravessar."], correctOption: 1, difficultyLevel: 1 },
  { text: "Se você ver um adulto atravessando a rua fora da faixa de pedestre, é correto você atravessar ali?", options: ["Sim, se o adulto estiver com pressa.", "Sim, se não estiver vindo nenhum carro.", "Sim, os adultos sempre sabem o que fazem.", "Não, o lugar mais seguro para atravessar é sempre na faixa de pedestre."], correctOption: 3, difficultyLevel: 1 },
  { text: "Para que serve o cinto de segurança no carro?", options: ["Para enfeitar o banco.", "Para segurar os brinquedos.", "Para proteger as pessoas em caso de sinistro.", "Para apoiar os braços."], correctOption: 2, difficultyLevel: 1 },
  { text: "O que você deve fazer quando estiver andando de bicicleta e ouvir a sirene de uma ambulância ou carro de bombeiros?", options: ["Continuar pedalando normalmente.", "Acelerar para sair da frente.", "Parar em um lugar seguro e esperar passar.", "Ir para o meio da rua para ver o que está acontecendo."], correctOption: 2, difficultyLevel: 1 },
  { text: "É correto jogar lixo pela janela do carro na via?", options: ["Sim, não faz mal para ninguém.", "Sim, se for um papel pequeno.", "Depende do que for o lixo.", "Não, é errado e pode sujar a cidade e causar sinistro."], correctOption: 3, difficultyLevel: 1 },
  { text: "O que o sinal vermelho do semáforo para pedestre indica?", options: ["A velocidade dos carros.", "Não é seguro atravessar a rua.", "O caminho que os ônibus devem seguir.", "O número de pessoas na rua."], correctOption: 1, difficultyLevel: 1 },
  { text: "Qual cor do sinal luminoso do semáforo para pedestres indica que você PODE atravessar a rua?", options: ["Vermelho.", "Verde.", "Amarelo.", "Roxo."], correctOption: 1, difficultyLevel: 1 },
  { text: "Se você estiver andando de bicicleta em grupo, o que é mais seguro?", options: ["Andar um do lado do outro no meio da rua.", "Andar um atrás do outro, na lateral da rua.", "Fazer corrida de bicicleta na calçada.", "Segurar na bicicleta do amigo para ir mais rápido."], correctOption: 1, difficultyLevel: 1 },
  { text: "O que fazer se a bola rolar para a rua?", options: ["Correr atrás da bola sem olhar.", "Pedir para o veículo parar para pegar a bola.", "Se tiver adulto pedir ajuda, ou atravessar com segurança.", "Esperar a bola voltar sozinha."], correctOption: 2, difficultyLevel: 1 },
  { text: "Para que servem os espelhos retrovisores nos veículos?", options: ["Para os motoristas se maquiarem.", "Para pendurar enfeites.", "Para os condutores verem o que está acontecendo atrás e dos lados.", "Para as crianças fazerem caretas."], correctOption: 2, difficultyLevel: 1 },
  { text: "É seguro atravessar a rua correndo entre os veículos parados no semáforo?", options: ["Sim, se os carros estiverem parados.", "Sim, se você for rápido.", "Talvez, se não vier nenhum carro.", "Não, é perigoso porque outro veículo pode começar a movimentar."], correctOption: 3, difficultyLevel: 1 },
  { text: "O que significa a placa com o desenho de bicicleta?", options: ["Que é proibido andar de carro ali.", "Que é um estacionamento para motos.", "Prioridade de circulação de ciclista.", "Que é um lugar para consertar bicicletas."], correctOption: 2, difficultyLevel: 1 },
  { text: "Se você estiver no carro e precisar desembarcar, qual lado é mais seguro para desembarcar se o carro estiver estacionado na via?", options: ["Qualquer porta, não importa.", "A porta do lado do motorista.", "A porta do lado da calçada.", "A porta da frente, mesmo estando no banco de trás."], correctOption: 2, difficultyLevel: 1 },
  { text: "O que você NÃO deve fazer quando estiver andando de skate ou patinete na rua?", options: ["Usar equipamentos de proteção como capacete e joelheiras.", "Prestar atenção nos pedestres e em outros veículos.", "Segurar em carros ou motos para ter impulso.", "Andar em locais planos e seguros."], correctOption: 2, difficultyLevel: 1 },
  { text: "Para que servem as luzes traseiras vermelhas nos carros e nas motos?", options: ["Para iluminar a rua para trás.", "Para que outros motoristas vejam o veículo à frente, especialmente à noite, com chuva ou neblina.", "Para indicar que o carro vai virar.", "Para enfeitar a parte de trás do veículo."], correctOption: 1, difficultyLevel: 1 },
  { text: "Se você ver um sinal de trânsito para pedestre, qual cor indica que os pedestres NÃO devem começar a atravessar?", options: ["Vermelho.", "Verde piscando.", "Verde aceso.", "Azul."], correctOption: 0, difficultyLevel: 1 },
  { text: "O que é importante fazer para atravessar uma via onde não tem faixa de pedestre?", options: ["Atravessar correndo o mais rápido possível.", "Fechar os olhos para não ter medo.", "Escolher um lugar onde você possa ver bem os dois lados da rua e esperar o momento seguro para atravessar.", "Acenar para os veículos pararem."], correctOption: 2, difficultyLevel: 1 },
  { text: "Qual destes acessórios NÃO é um equipamento de segurança importante para usar no carro?", options: ["Cinto de segurança.", "Cadeirinha para crianças.", "Encosto de cabeça.", "Insulfilm nos vidros."], correctOption: 3, difficultyLevel: 1 },
  //Motociclistas
  { text: "Quais são as penalidades previstas para motociclistas que avançam o sinal vermelho em Campo Grande?", options: ["Multa, pontos na CNH e possível suspensão do direito de dirigir.", "Somente advertência verbal.", "Apenas multa sem pontuação.", "Nenhuma penalidade específica."], correctOption: 0, difficultyLevel: 3 },
  { text: "Quais equipamentos obrigatórios o motociclista deve usar para conduzir com segurança e conforme a legislação de Campo Grande?", options: ["Capacete com viseira, luvas, jaqueta e botas específicas.", "Apenas capacete simples.", "Capacete aberto e tênis.", "Qualquer equipamento de proteção é opcional."], correctOption: 0, difficultyLevel: 3 },
  { text: "Em situações de neblina ou baixa visibilidade, qual a conduta correta ao pilotar motocicleta?", options: ["Aumentar a velocidade para chegar mais rápido ao destino.", "Reduzir a velocidade, ligar faróis baixos e manter atenção redobrada.", "Desligar os faróis para evitar ofuscamento.", "Pilotar normalmente, pois a visibilidade não interfere."], correctOption: 1, difficultyLevel: 3 },
  { text: "Como deve ser feita a manutenção periódica da motocicleta para garantir segurança e conformidade em Campo Grande?", options: ["Verificar freios, pneus, luzes, sistema de escape e outros itens essenciais regularmente, conforme recomendação do fabricante.", "Apenas trocar o óleo quando o motor falhar.", "Manutenção somente após acidentes.", "Somente revisão anual no posto de combustível."], correctOption: 0, difficultyLevel: 3 },
  { text: "Qual a importância do uso correto do escapamento e do silenciador na motocicleta, conforme legislação de Campo Grande?", options: ["Evitar ruídos excessivos que perturbam o sossego público e evitar penalidades legais.", "Para deixar a moto mais rápida.", "Para melhorar o consumo de combustível.", "Para personalizar a motocicleta."], correctOption: 0, difficultyLevel: 3 }
